import React from "react";
import Chart from "chart.js/auto";
import { Bar } from "react-chartjs-2";
import "./App.css";

const jsonData = require("./data.json");
let labels = jsonData.map(function(e) { return e.day })
let price = jsonData.map(function(e) { return e.amount })

Chart.register();

function color() {
  let backgrounds = Array(7).fill("hsl(10, 79%, 65%)");
  let backgroundsHover = Array(7).fill("hsl(33, 100%, 98%)");
  let date = new Date();
  date.setDate(date.getDate() - 1);
  let today = date.getDay(); 

  backgrounds[today] = "hsl(186, 34%, 60%)";

  return [backgrounds, backgroundsHover];
}

let index = color();

export const data = {
  labels: labels,
  datasets: [
    {
      data: price,
      backgroundColor: backgrounds,
      hoverBackgroundColor: "hsl(33, 100%, 98%)",
      borderRadius: 5,
      borderSkipped: false,
    },
  ],
};

export const options = {
  scales: {
    x: {
      border: {
        color: "white",
      },
      ticks: {
        color: "hsl(28, 10%, 53%)",
        font: {
          size: "13%",
        },
      },
      grid: {
        display: false,
      },
    },
    y: {
      display: false,
      grid: {
        display: false,
      },
    },
  },
  plugins: {
    legend: {
      display: false,
    },
    tooltip: {
      callbacks: {
        label: function(context) {
            let label = context.dataset.label || '';

            if (label) {
                label += ': ';
            }
            if (context.parsed.y !== null) {
                label += new Intl.NumberFormat('en-US', 
                { style: 'currency', currency: 'USD' }).format(context.parsed.y);
            }
            return label;
        }
      }
    },
    responsive: true,
  }
}

export default function App (){

return (
  <Bar
    options={options}
    data={data}
  />
);
}
