import Chart from "chart.js/auto";
import "./App.css";

Chart.register();

export const data = {
  labels: ["mon", "thu", "wed", "thu", "fri", "sat", "sun"],
  datasets: [
    {
      data: [15, 35, 52.36, 31.07, 25, 48, 28],
      backgroundColor: ["hsl(10, 79%, 65%)"],
      hoverBackgroundColor: ["rgba(255,155,135,255)"],
      borderRadius: 5,
      borderSkipped: false,
    },
  ],
};

export const options = {
  scales: {
    x: {
      border: {
        color: "white",
      },
      ticks: {
        color: "hsl(28, 10%, 53%)",
        font: {
          size: "13%",
        },
      },
      grid: {
        display: false,
      },
    },
    y: {
      display: false,
      grid: {
        display: false,
      },
    },
  },
  plugins: {
    legend: {
      display: false,
    },
    tooltip: {
      enabled: false,
      backgroundColor: "hsl(186, 34%, 60%)",
    },
  },
  elements: {
    rectangle: {
        hoverBackgroundColor: function(context) {
            var index = context.datasetIndex;
            var hoverColor = "hsl(186, 34%, 60%)"; 
            return index === 0 ? hoverColor : "hsl(33, 100%, 98%)";
        }
    }
  },
  responsive: true,
};

export default function App() {}
