import React, { useRef } from "react";
import Chart from "chart.js/auto";
import { Bar } from "react-chartjs-2";
import "./App.css";

Chart.register();

export const data = {
  labels: ["mon", "thu", "wed", "thu", "fri", "sat", "sun"],
  datasets: [
    {
      data: [15, 35, 52.36, 31.07, 25, 48, 28],
      backgroundColor: ["hsl(10, 79%, 65%)"],
      hoverBackgroundColor: ["rgba(255,155,135,255)"],
      borderRadius: 5,
      borderSkipped: false,
    },
  ],
};

export const options = {
  scales: {
    x: {
      border: {
        color: "white",
      },
      ticks: {
        color: "hsl(28, 10%, 53%)",
        font: {
          size: "13%",
        },
      },
      grid: {
        display: false,
      },
    },
    y: {
      display: false,
      grid: {
        display: false,
      },
    },
  },
  plugins: {
    legend: {
      display: false,
    },
    tooltip: {
      enabled: false,
      backgroundColor: "hsl(186, 34%, 60%)",
    },
  },
  responsive: true,
}

export default function App() {
  const chartRef = useRef(null);

  function handleHover(evt, item) {
    if (item.length) {
      const datasetIndex = item[0].datasetIndex;
      const dataIndex = item[0].index;
      chartRef.current.chartInstance.data.datasets[datasetIndex].backgroundColor[dataIndex] = "red";
      chartRef.current.chartInstance.update();
    }
  }

  function handleLeave() {
    chartRef.current.chartInstance.data.datasets[0].backgroundColor = ["hsl(186, 34%, 60%)"];
    chartRef.current.chartInstance.update();
  }

  return (
    <Bar
      ref={chartRef}
      options={options}
      data={data}
    />
  );
}
