import React, { useRef} from "react";
import Chart from "chart.js/auto";
import { Bar, getElementAtEvent } from "react-chartjs-2";
import "./App.css";

const jsonData = require("./data.json");
let labels = jsonData.map(function(e) { return e.day })
let price = jsonData.map(function(e) { return e.amount })

Chart.register();

export const data = {
  labels: labels,
  datasets: [
    {
      data: price,
      backgroundColor: ["hsl(10, 79%, 65%)",
                        "hsl(10, 79%, 65%)",
                        "hsl(186, 34%, 60%)",
                        "hsl(10, 79%, 65%)",
                        "hsl(10, 79%, 65%)",
                        "hsl(10, 79%, 65%)",
                        "hsl(10, 79%, 65%)"],
      hoverBackgroundColor: ["rgba(255,155,135,255)",
                            "rgba(255,155,135,255)", 
                            "rgba(180,223,229,255)",
                            "rgba(255,155,135,255)", 
                            "rgba(255,155,135,255)", 
                            "rgba(255,155,135,255)", 
                            "rgba(255,155,135,255)" ],
      borderRadius: 5,
      borderSkipped: false,
    },
  ],
};

export const options = {
  scales: {
    x: {
      border: {
        color: "white",
      },
      ticks: {
        color: "hsl(28, 10%, 53%)",
        font: {
          size: "13%",
        },
      },
      grid: {
        display: false,
      },
    },
    y: {
      display: false,
      grid: {
        display: false,
      },
    },
  },
  plugins: {
    legend: {
      display: false,
    },
    tooltip: {
      callbacks: {
        label: function(context) {
            let label = context.dataset.label || '';

            if (label) {
                label += ': ';
            }
            if (context.parsed.y !== null) {
                label += new Intl.NumberFormat('en-US', 
                { style: 'currency', currency: 'USD' }).format(context.parsed.y);
            }
            return label;
        }
      }
    },
    responsive: true,
  }
}

export default function App (){

return (
  <Bar
    options={options}
    data={data}
  />
);
}
