import React from "react";
import Chart from "chart.js/auto";
import { Bar } from "react-chartjs-2";
import "./App.css";

const jsonData= require("./data.json");
let labels = jsonData.map(function(e) { return e.day });
let price = jsonData.map(function(e) { return e.amount });

const HOVER_INDEX = 1;
const BACKGROUND_INDEX = 0;

Chart.register();

function currentDayBackground() {
  let backgroundTable = Array(7).fill("hsl(10, 79%, 65%)");
  let hoverBackgroundTable = Array(7).fill("rgba(255,155,135,255)");
  const date = new Date();
  date.setDate(date.getDate() - 1);
  const day = date.getDay(); 

  backgroundTable[day] = "hsl(186, 34%, 60%)";
  hoverBackgroundTable[day] = "rgba(180,223,229,255)";

  return [backgroundTable, hoverBackgroundTable];
}

let backgroundTables = currentDayBackground();

export const data = {
  labels: labels,
  datasets: [
    {
      data: price,
      backgroundColor: backgroundTables[BACKGROUND_INDEX],
      hoverBackgroundColor: backgroundTables[HOVER_INDEX],
      borderRadius: 5,
      borderSkipped: false,
    },
  ],
};


export const options = {
  scales: {
    x: {
      border: {
        color: "white",
      },
      ticks: {
        color: "hsl(28, 10%, 53%)",
        font: {
          size: "13%",
        },
      },
      grid: {
        display: false,
      },
    },
    y: {
      display: false,
      grid: {
        display: false,
      },
    },
  },
  plugins: {
    legend: {
      display: false,
    },
    tooltip: {
      position: 'nearest',
      mode: 'index',
      intersect: false,
      callbacks: {
        label: function(context) {
            let label = context.dataset.label || '';
            if (label) {
                label += ': ';
            }
            if (context.parsed.y !== null) {
                label += new Intl.NumberFormat('en-US', 
                { style: 'currency', currency: 'USD' }).format(context.parsed.y);
            }
            return label;
        },
      },
      custom: function(tooltipModel) {
        var tooltipEl = document.getElementById('chartjs-tooltip');

        if (!tooltipEl) {
            tooltipEl = document.createElement('div');
            tooltipEl.id = 'chartjs-tooltip';
            tooltipEl.style.position = 'absolute';
            tooltipEl.style.pointerEvents = 'none';
            document.body.appendChild(tooltipEl);
        }

        if (tooltipModel.opacity === 0) {
            tooltipEl.style.opacity = 0;
            return;
        }

        var position = this._chart.canvas.getBoundingClientRect();

        tooltipEl.style.opacity = 1;
        tooltipEl.style.left = position.left + window.pageXOffset + tooltipModel.caretX + 'px';
        tooltipEl.style.top = position.top + window.pageYOffset + tooltipModel.caretY - tooltipEl.offsetHeight - 5 + 'px';
        tooltipEl.style.fontFamily = tooltipModel._bodyFontFamily;
        tooltipEl.style.fontSize = tooltipModel.bodyFontSize + 'px';
        tooltipEl.style.fontStyle = tooltipModel._bodyFontStyle;
        tooltipEl.style.padding = tooltipModel.yPadding + 'px ' + tooltipModel.xPadding + 'px';
        tooltipEl.style.backgroundColor = tooltipModel.backgroundColor;
        tooltipEl.style.borderRadius = tooltipModel.cornerRadius + 'px';

        var innerHtml = '';
        if (tooltipModel.body) {
            var titleLines = tooltipModel.title || [];
            var bodyLines = tooltipModel.body.map(function(bodyItem) {
                return bodyItem.lines;
            });

            innerHtml += '<div>';

            titleLines.forEach(function(title) {
                innerHtml += '<span>' + title + '</span>';
            });
            bodyLines.forEach(function(body, i) {
                var colors = tooltipModel.labelColors[i];
                var style = 'background:' + colors.backgroundColor;
                style += '; border-color:' + colors.borderColor;
                style += '; border-width: 2px';
                var span = '<span style="' + style + '"></span>';
                innerHtml += '<span>' + span + body + '</span>';
            });
            innerHtml += '</div>';
        }

        tooltipEl.innerHTML = innerHtml;
    },
    },
    responsive: true,
  }
}

export default function App (){

return (
  <Bar
    options={options}
    data={data}
  />
);
}
